import { use, useState } from "react";
import "./style.css";

const initialFacts = [
  {
    id: 1,
    text: "React is being developed by Meta (formerly facebook)",
    source: "https://opensource.fb.com/",
    category: "technology",
    votesInteresting: 24,
    votesMindblowing: 9,
    votesFalse: 4,
    createdIn: 2021,
  },
  {
    id: 2,
    text: "Millennial dads spend 3 times as much time with their kids than their fathers spent with them. In 1982, 43% of fathers had never changed a diaper. Today, that number is down to 3%",
    source:
      "https://www.mother.ly/parenting/millennial-dads-spend-more-time-with-their-kids",
    category: "society",
    votesInteresting: 11,
    votesMindblowing: 2,
    votesFalse: 0,
    createdIn: 2019,
  },
  {
    id: 3,
    text: "Lisbon is the capital of Portugal",
    source: "https://en.wikipedia.org/wiki/Lisbon",
    category: "society",
    votesInteresting: 8,
    votesMindblowing: 3,
    votesFalse: 1,
    createdIn: 2015,
  },
];

const CATEGORIES = [
  { name: "technology", color: "#3b82f6" },
  { name: "science", color: "#16a34a" },
  { name: "finance", color: "#ef4444" },
  { name: "society", color: "#eab308" },
  { name: "entertainment", color: "#db2777" },
  { name: "health", color: "#14b8a6" },
  { name: "history", color: "#f97316" },
  { name: "news", color: "#8b5cf6" },
];

function isValidHttpUrl(string) {
  let url;

  try {
    url = new URL(string);
  } catch (_) {
    return false;
  }

  return url.protocol === "http:" || url.protocol === "https:";
}

function getCategoryColor(categoryName) {
  return (CATEGORIES.find((category) => category.name === categoryName) || {})
    .color;
}

function App() {
  const [showForm, setShowForm] = useState(false);

  return (
    <>
      <Header showForm={showForm} setShowForm={setShowForm} />
      {showForm ? <NewFactForm /> : null}
      <main>
        <CategoryFilter />
        <FactList />
      </main>
    </>
  );
}

function Header({ showForm, setShowForm }) {
  return (
    <header>
      <div className="logo">
        <img src="logo.png" width="68" height="68" alt="Logo" />
        <h1>Knowledge Forum</h1>
      </div>
      <button
        className="btn btn-large"
        id="show-form-btn"
        onClick={() => setShowForm((show) => !show)}
      >
        {showForm ? "Close" : "Share a fact"}
      </button>
    </header>
  );
}

function NewFactForm() {
  const [factText, setFactText] = useState("");
  const [factSource, setFactSource] = useState("");
  const [category, setCategory] = useState("");

  function handleSubmit(e) {
    // 1. Prevent browser reload
    e.preventDefault(); // Default is reload

    //2. check if data is valid
    if (
      factText &&
      isValidHttpUrl(factSource) &&
      category &&
      factText.length <= 200
    ) {
      console.log(factText, factSource, category);
    }

    //3. create the new fact

    //4. add the new fact to the ui (stare)

    //5. Reset the input field

    //6. Close the form
  }

  return (
    <form className="fact-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Share a fact"
        onChange={(e) => setFactText(e.target.value)}
      />{" "}
      <span>{200 - factText.length}</span>
      <input
        type="text"
        placeholder="Trusworthy source"
        onChange={(e) => setFactSource(e.target.value)}
      />
      <select value={category} onChange={(e) => setCategory(e.target.value)}>
        <option value="">Chose category</option>
        {CATEGORIES.map((cat) => (
          <option key={cat.name} value={cat.name}>
            {cat.name.toUpperCase()}
          </option>
        ))}
      </select>
      <button className="btn btn-large">Post</button>
    </form>
  );
}

function CategoryFilter() {
  return (
    <aside>
      <ul>
        <button className="btn btn-category">All</button>
        {CATEGORIES.map((elem) => (
          <li key={elem.name}>
            <button
              className="btn btn-category"
              style={{ backgroundColor: elem.color }}
            >
              {elem.name}
            </button>
          </li>
        ))}
      </ul>
    </aside>
  );
}

function FactList() {
  const facts = initialFacts;

  return (
    <section>
      <ul className="fact-list">
        {facts.map((fact) => (
          <Fact key={fact.id} fact={fact} />
        ))}
      </ul>
    </section>
  );
}

function Fact({ fact }) {
  return (
    <li className="fact">
      <p>
        {fact.text}
        <a className="source" href={fact.source} target="_blank">
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{ backgroundColor: getCategoryColor(fact.category) }}
      >
        {fact.category}
      </span>
      <ul className="reactions">
        <li>
          <button>üëç {fact.votesInteresting}</button>
        </li>
        <li>
          <button>ü§Ø {fact.votesMindblowing}</button>
        </li>
        <li>
          <button>‚ùå {fact.votesFalse}</button>
        </li>
      </ul>
    </li>
  );
}

export default App;
